import ProductService from '../services/ProductService'
import Util from '../utils/Utils'

const util = new Util()


const getAllProducts = async (req, res) => {
  try {
    const allProducts = await ProductService.getAllProducts()
    if (allProducts.length > 0) {
      util.setSuccess(200, 'Products retrieved', allProducts)
    } else {
      util.setSuccess(200, 'No Product found')
    }
    return util.send(res)
  } catch (error) {
    util.setError(400, error)
    return util.send(res)
  }
}
const addProduct = async (req, res) => {
  if (!req.body.name || !req.body.price || !req.body.type || !typeof(req.body.breakfast)===Boolean) {
    util.setError(400, 'Please provide complete details')
    return util.send(res)
  }
  const newProduct = req.body
  try {
    const createdProduct = await ProductService.addProduct(newProduct)
    util.setSuccess(201, 'Product Added!', createdProduct)
    return util.send(res)
  } catch (error) {
    util.setError(400, error.message)
    return util.send(res)
  }
}

const updatedProduct = async (req, res) => {
  const alteredProduct = req.body
  const { id } = req.params
  if (!Number(id)) {
    util.setError(400, 'Please input a valid numeric value')
    return util.send(res)
  }
  try {
    const updateProduct = await ProductService.updateProduct(id, alteredProduct)
    if (!updateProduct) {
      util.setError(404, `Cannot find product with the id: ${id}`)
    } else {
      util.setSuccess(200, 'Product updated', updateProduct)
    }
    return util.send(res)
  } catch (error) {
    util.setError(404, error)
    return util.send(res)
  }
}

const getProduct = async (req, res) => {
  const { id } = req.params

  if (!Number(id)) {
    util.setError(400, 'Please input a valid numeric value')
    return util.send(res)
  }

  try {
    const theProduct = await ProductService.getProduct(id)

    if (!theProduct) {
      util.setError(404, `Cannot find Product with the id ${id}`)
    } else {
      util.setSuccess(200, 'Found Product', theProduct)
    }
    return util.send(res)
  } catch (error) {
    util.setError(404, error)
    return util.send(res)
  }
}

const deleteProduct = async(req, res) => {
  console.log('oi');

  const { id } = req.params

  if (!Number(id)) {
    util.setError(400, 'Please provide a numeric value')
    return util.send(res)
  }

  try {
    const productToDelete = await ProductService.deleteProduct(id)

    if (productToDelete) {
      util.setSuccess(200, 'Product deleted')
    } else {
      util.setError(404, `Product with the id ${id} cannot be found`)
    }
    return util.send(res)
  } catch (error) {
    util.setError(400, error)
    return util.send(res)
  }
}


export default {
  getAllProducts,
  addProduct,
  updatedProduct,
  getProduct,
  deleteProduct
}

import database from '../src/models'

class ProductService {
  static async getAllProducts() {
    try {
      return await database.product.findAll()
    } catch (error) {
      throw error
    }
  }

  static async addProduct(newProduct) {
    try {      
      return await database.product.create(newProduct)
    } catch (error) {
      throw error
    }
  }

  static async updateProduct(id, updateProduct) {
    try {
      const productToUpdate = await database.product.findOne({
        where: { id: Number(id) }
      })

      if (productToUpdate) {
        await database.product.update(updateProduct, { where: { id: Number(id) } })

        return updateProduct
      }
      return null
    } catch (error) {
      throw error
    }
  }

  static async getProduct(id) {
    try {
      const theProduct = await database.product.findOne({
        where: { id: Number(id) }
      })

      return theProduct
    } catch (error) {
      throw error
    }
  }

  static async deleteProduct(id) {
    try {      
      const productToDelete = await database.product.findOne({ where: { id: Number(id) } })

      if (productToDelete) {
        const deletedProduct = await database.product.destroy({
          where: { id: Number(id) }
        })
        return deletedProduct
      }
      return null
    } catch (error) {
      throw error
    }
  }
}

export default ProductService

